var mongode = require('mongode');
//var config = require('config.json');
// trying to commit a change to heroku again
var mongohqURL = process.env.MONGOHQ_URL
var databaseURL = mongohqURL;
//var databaseURL = config.mdbString;

function getDB() {
  var db = mongode.connect(databaseURL);
  //var dbcon = mongode.hungryp_mongo;
  return db;
}

function getOID(id) {
  //var db = mongode.connect(databaseURL);
  //var dbcon = mongode.hungryp_mongo;
  return new mongode.ObjectID(id);
}

function getCursor() {
  //var db = mongode.connect(databaseURL);
  //var dbcon = mongode.hungryp_mongo;
  return mongode.Cursor();
}

exports.getDB = getDB;
exports.getOID = getOID;