var http = require("http"),
    url = require("url"),
    router = require('router'),
    port = process.argv[2] || 11329;

function start(route, handle) {
  http.createServer(function(request, response) {
    var wp = url.parse(request.url).pathname.split('/')[1];
    var pathname = (wp === 'MHCreatives') ? '/' : url.parse(request.url).pathname;
    if (url.parse(request.url).pathname.split('/')[2] === 'rdata') {
	  pathname = '/rdata';                   //reset password response page
    }
    if (url.parse(request.url).pathname.split('/')[2] === 'ndata') {
	  pathname = '/ndata';                   //confirm new registration set dateExpire response page
    }	
    route(handle, pathname, request, response);

  }).listen(port);

  console.log("server => http://localhost:" + port + "/\nCTRL + C to shutdown");

}

exports.start = start;
